-- Дамп структуры для таблица worldofmind.skills
CREATE TABLE IF NOT EXISTS `skills` (
  `id` int NOT NULL AUTO_INCREMENT,
  `user_id` int NOT NULL,
  `article` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `category` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `description` text COLLATE utf8mb4_unicode_ci,
  `text` longtext COLLATE utf8mb4_unicode_ci,
  `color` varchar(20) COLLATE utf8mb4_unicode_ci DEFAULT '#ffffff',
  `image` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `idx_skills_user_id` (`user_id`),
  KEY `idx_skills_article` (`article`),
  KEY `idx_skills_category` (`category`),
  KEY `idx_skills_created_at` (`created_at`),
  FULLTEXT KEY `idx_skills_text_search` (`article`,`description`,`text`),
  CONSTRAINT `skills_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Дамп данных таблицы worldofmind.skills: ~3 rows (приблизительно)
INSERT INTO `skills` (`id`, `user_id`, `article`, `category`, `description`, `text`, `color`, `image`, `created_at`, `updated_at`) VALUES
	(5, 2, 'Природоведение', 'Наука', 'Информация о природоведении. Это начальные сведения по наукам: биология, география, геология, экология, астрономия, физика и химия.', '<p><strong>Природа</strong> — это всё, что существует вокруг нас и не создано человеком.</p><h2>Живое и неживое</h2><p><strong>Живые объекты</strong> — это объекты природы, которые обладают всеми признаками жизни: рост. размножение, питание. дыхание. реакция на раздражители, обмен веществ.<br><strong>Неживые объекты</strong> — это объекты природы или созданные человеком предметы, которые не обладают всеми признаками жизни: не растут, не питаются, не дышат, не размножаются и не реагируют активно на изменения окружающей среды.</p><h3><strong>Отличия живой и неживой природы:</strong></h3><table style="width: 437px;"><colgroup><col style="width: 129px;"><col style="width: 146px;"><col style="width: 162px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="129"><p>Признак</p></th><th colspan="1" rowspan="1" colwidth="146"><p>Живая природа</p></th><th colspan="1" rowspan="1" colwidth="162"><p>Неживая природа</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Рост</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Да</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Размножение</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Да</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Движение</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Часто</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет (кроме внешних сил, как ветер)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Питание</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Да</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет</p></td></tr></tbody></table><p><strong>Минералы</strong> — твердые природные вещества, которые образуются в земле. Не живые, но важны для жизни. Примеры: соль, кварц, уголь.</p><h2>Растения</h2><p><strong>Растения</strong> — живые организмы, которые умеют <strong>фотосинтезировать</strong> (создавать пищу из света, воды и углекислого газа). Примеры: дерево, трава, цветок.</p><h3>Строение растений</h3><table style="width: 555px;"><colgroup><col style="width: 100px;"><col style="width: 455px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="100"><p>Части</p></th><th colspan="1" rowspan="1" colwidth="455"><p>Функции</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Корень</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Закрепляет растение, всасывает воду и минеральные вещества</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Стебель</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Держит листья, проводит воду и питательные вещества</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Листья</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Производят пищу с помощью фотосинтеза</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Цветок</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Орган размножения, образует семена</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Плод</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Защищает семена, помогает их распространению</p></td></tr></tbody></table><h2>Животные</h2><p><strong>Животные</strong> — живые организмы, которые <strong>двигаются и питаются готовой пищей</strong>, могут реагировать на окружающую среду. Примеры: кошка, птица, рыба.</p><table style="width: 334px;"><colgroup><col style="width: 150px;"><col style="width: 184px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="150"><p>Тип</p></th><th colspan="1" rowspan="1" colwidth="184"><p>Примеры</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="150"><p>Позвоночные</p></td><td colspan="1" rowspan="1" colwidth="184"><p>Лев, слон, рыба</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="150"><p>Беспозвоночные</p></td><td colspan="1" rowspan="1" colwidth="184"><p>Медуза, паук, муравей</p></td></tr></tbody></table><h2>Вода в природе</h2><p><strong>Вода</strong> — прозрачная жидкость, необходимая для жизни всех живых организмов.</p><h3>Виды водоёмов</h3><table style="width: 474px;"><colgroup><col style="width: 132px;"><col style="width: 342px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="132"><p>Тип водоёма</p></th><th colspan="1" rowspan="1" colwidth="342"><p>Особенности</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Река</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Текущая вода, соединяет озёра и моря</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Озеро</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Стоячая вода, может быть пресной или солёной</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Море</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Солёная вода, часть океана</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Океан</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Самый большой водоём, солёная вода</p></td></tr></tbody></table><img class="tiptap-resizable-image" src="http://localhost:5000/uploads/e9b46ecf-51d1-41cc-8ca5-3d416d4e0cdc.png" width="451" height="301"><h2>Воздух и атмосфера</h2><p><strong>Воздух</strong> — смесь газов, окружающая Землю, необходимая для дыхания всех живых существ.</p><p><strong>Атмосфера</strong> — газовая оболочка Земли, состоящая из слоёв воздуха.</p><h2>Погода и климат</h2><p><strong>Погода</strong> — состояние атмосферы в определённом месте и времени (дождь, снег, солнечно, ветер).</p><p><strong>Климат</strong> — среднее состояние погоды на большой территории за длительное время (годы, десятилетия).</p><h2>Почва</h2><p><strong>Почва</strong> — верхний слой земли, на котором растут растения.</p><h3>Состав почвы</h3><table style="width: 704px;"><colgroup><col style="width: 185px;"><col style="width: 519px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="185"><p>Компонент</p></th><th colspan="1" rowspan="1" colwidth="519"><p>Функция</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Минеральные вещества</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Дают растениям необходимые элементы (например, калий, фосфор, азот)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Вода</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Необходима для питания и жизни растений</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Воздух</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Корни дышат, получают кислород</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Органические вещества</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Улучшают структуру почвы, делают её плодородной</p></td></tr></tbody></table><h2>Солнечные циклы Земли</h2><p><strong>День</strong> — период времени, когда Солнце видно над горизонтом.</p><p><strong>Ночь</strong> — период времени, когда Солнце находится ниже горизонта и свет не достигает</p><p><strong>Вращение Земли вокруг оси</strong> — движение, которое вызывает смену дня и ночи.</p><div data-block-type="note" data-type="info-block" class="info-block info-block-note"><ul><li><p>Земля вращается вокруг своей оси за <strong>24 часа</strong>.</p></li><li><p>Та часть Земли, которая повернута к Солнцу, испытывает <strong>день</strong>, противоположная часть — <strong>ночь</strong>.</p></li></ul></div><p><strong>Ось Земли</strong> — воображаемая линия, вокруг которой вращается Земля.</p><p><strong>Вращение Земли вокруг Солнца</strong> — движение, которое создаёт смену времён года.</p><div data-block-type="note" data-type="info-block" class="info-block info-block-note"><ul><li><p>Земля вращается вокруг Солнца за <strong>1 год</strong>.</p></li><li><p>Ось Земли наклонена примерно на <strong>23,5°</strong>, поэтому в разные времена года Солнце освещает разные части планеты по-разному.</p></li></ul></div><h2>Природные явления: дождь, снег, гроза, ветер</h2><p><strong>Природное явление</strong> — событие или процесс, происходящий в природе, без участия человека.</p><table style="width: 990px;"><colgroup><col style="width: 125px;"><col style="width: 383px;"><col style="width: 482px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="125"><p>Явление</p></th><th colspan="1" rowspan="1" colwidth="383"><p>Определение</p></th><th colspan="1" rowspan="1" colwidth="482"><p>Влияние на природу</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Дождь</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Осадки в виде капель воды</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Увлажняет почву, обеспечивает растения и животных водой</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Снег</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Осадки в виде кристаллов льда</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Увлажняет почву, формирует снежный покров, обеспечивает водой</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Гроза</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Атмосферное явление с дождём, громом и молниями</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Формирует дождевые осадки, может быть опасна для животных и людей</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Ветер</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Движение воздуха</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Переносит семена, пыльцу, влияет на погоду и климат</p></td></tr></tbody></table><h2>Охрана природы</h2><p><strong>Охрана природы</strong> — действия человека, направленные на сохранение природы и её ресурсов.</p><p><strong>Природные ресурсы</strong> — всё, что человек использует из природы: вода, растения, животные, воздух, почва.</p><p><strong>Экологическая ответственность</strong> — понимание влияния своих действий на природу и старание не причинять ей вред.</p><h3>Основные способы охраны природы</h3><table style="width: 684px;"><colgroup><col style="width: 164px;"><col style="width: 520px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="164"><p>Способ</p></th><th colspan="1" rowspan="1" colwidth="520"><p>Пример действий</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Сохранение воды</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не оставлять краны открытыми, использовать дождевую воду для полива</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Экономия энергии</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Выключать свет, использовать энергоэффективные лампы</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Защита растений</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не вырывать цветы, сажать новые растения</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Защита животных</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не обижать животных, помогать птицам зимой</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Чистота и порядок</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не бросать мусор, участвовать в субботниках</p></td></tr></tbody></table><p></p>', '#FDFF73', NULL, '2025-08-19 21:24:40', '2025-08-29 14:38:27'),
	(6, 2, 'Github', 'Программирование', 'Полная информация о работе с Git. Так же про Github и инструменты, такие как GitLab.', '<p><strong>Git</strong> — это распределённая система управления версиями, которая позволяет отслеживать изменения в файлах и координировать совместную работу над проектами.<br><strong>GitHub</strong> — это веб-платформа и облачный сервис для хранения, управления и совместной работы с Git-репозиториями.</p><h2>Git Bash</h2><h3>Инициализация и настройка</h3><p><strong>git init</strong> - создаёт новый локальный репозиторий.</p><p><strong>git clone &lt;url&gt;</strong> -&nbsp; клонирует удалённый репозиторий на ваш компьютер.</p><p><strong>git config --global user.name "Username" </strong>- задаёт имя автора коммитов.</p><p><strong>git config --global user.email "example@domain,com"</strong> -&nbsp;задаёт почту автора.</p><h3>Работа с изменениями</h3><p><strong>git status </strong>- показывает текущее состояние репозитория (изменённые, добавленные, неотслеживаемые файлы).</p><p><strong>git add &lt;filename&gt; </strong>-&nbsp;добавляет файл в индекс для следующего коммита.</p><p><strong>git add .</strong> -&nbsp;добавляет все изменения в текущей папке.</p><p><strong>git commit -m "message" </strong>-&nbsp;создаёт коммит с описанием изменений.</p><h3>Работа с удалённым репозиторием</h3><p><strong>git push</strong> - отправка локальных изменений в удалённый репозиторий.</p><p><strong>git pull </strong>-&nbsp;получение и слияние изменений с удалённого репозитория.</p>', '#FDFF73', NULL, '2025-08-19 21:28:28', '2025-08-20 12:55:29'),
	(7, 2, 'пввар', 'Программирование', 'пквпва', '<p>пап</p>', '#FDFF73', NULL, '2025-08-26 13:20:04', '2025-08-26 13:20:04');

-- Дамп структуры для таблица worldofmind.users
CREATE TABLE IF NOT EXISTS `users` (
  `id` int NOT NULL AUTO_INCREMENT,
  `username` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `password` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`),
  KEY `idx_users_username` (`username`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

