-- Дамп структуры для таблица worldofmind.skills
CREATE TABLE IF NOT EXISTS `skills` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `article` varchar(255) NOT NULL,
  `category` varchar(100) NOT NULL,
  `description` text DEFAULT NULL,
  `text` longtext DEFAULT NULL,
  `color` varchar(20) DEFAULT '#ffffff',
  `image` varchar(255) DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `category_id` int(10) unsigned DEFAULT NULL,
  `subcategory_id` int(10) unsigned DEFAULT NULL,
  `position_x` float DEFAULT 0,
  `position_y` float DEFAULT 0,
  PRIMARY KEY (`id`),
  KEY `idx_skills_user_id` (`user_id`),
  KEY `idx_skills_article` (`article`),
  KEY `idx_skills_category` (`category`),
  KEY `idx_skills_created_at` (`created_at`),
  KEY `idx_skills_category_id` (`category_id`),
  KEY `idx_skills_subcategory_id` (`subcategory_id`),
  FULLTEXT KEY `idx_skills_text_search` (`article`,`description`,`text`),
  CONSTRAINT `skills_ibfk_1` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`) ON DELETE CASCADE,
  CONSTRAINT `skills_ibfk_2` FOREIGN KEY (`category_id`) REFERENCES `categories` (`id`) ON DELETE SET NULL,
  CONSTRAINT `skills_ibfk_3` FOREIGN KEY (`subcategory_id`) REFERENCES `subcategories` (`id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Дамп данных таблицы worldofmind.skills: ~10 rows (приблизительно)
DELETE FROM `skills`;
INSERT INTO `skills` (`id`, `user_id`, `article`, `category`, `description`, `text`, `color`, `image`, `created_at`, `updated_at`, `category_id`, `subcategory_id`, `position_x`, `position_y`) VALUES
	(5, 2, 'Природоведение', 'Наука', 'Информация о природоведении. Это начальные сведения по наукам: биология, география, геология, экология, астрономия, физика и химия.', '<p><strong>Природа</strong> — это всё, что существует вокруг нас и не создано человеком.</p><h2>Живое и неживое</h2><p><strong>Живые объекты</strong> — это объекты природы, которые обладают всеми признаками жизни: рост. размножение, питание. дыхание. реакция на раздражители, обмен веществ.<br><strong>Неживые объекты</strong> — это объекты природы или созданные человеком предметы, которые не обладают всеми признаками жизни: не растут, не питаются, не дышат, не размножаются и не реагируют активно на изменения окружающей среды.</p><h3><strong>Отличия живой и неживой природы:</strong></h3><table style="width: 437px;"><colgroup><col style="width: 129px;"><col style="width: 146px;"><col style="width: 162px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="129"><p>Признак</p></th><th colspan="1" rowspan="1" colwidth="146"><p>Живая природа</p></th><th colspan="1" rowspan="1" colwidth="162"><p>Неживая природа</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Рост</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Да</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Размножение</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Да</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Движение</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Часто</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет (кроме внешних сил, как ветер)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="129"><p>Питание</p></td><td colspan="1" rowspan="1" colwidth="146"><p>Да</p></td><td colspan="1" rowspan="1" colwidth="162"><p>Нет</p></td></tr></tbody></table><p><strong>Минералы</strong> — твердые природные вещества, которые образуются в земле. Не живые, но важны для жизни. Примеры: соль, кварц, уголь.</p><h2>Растения</h2><p><strong>Растения</strong> — живые организмы, которые умеют <strong>фотосинтезировать</strong> (создавать пищу из света, воды и углекислого газа). Примеры: дерево, трава, цветок.</p><h3>Строение растений</h3><table style="width: 555px;"><colgroup><col style="width: 100px;"><col style="width: 455px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="100"><p>Части</p></th><th colspan="1" rowspan="1" colwidth="455"><p>Функции</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Корень</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Закрепляет растение, всасывает воду и минеральные вещества</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Стебель</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Держит листья, проводит воду и питательные вещества</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Листья</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Производят пищу с помощью фотосинтеза</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Цветок</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Орган размножения, образует семена</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="100"><p>Плод</p></td><td colspan="1" rowspan="1" colwidth="455"><p>Защищает семена, помогает их распространению</p></td></tr></tbody></table><h2>Животные</h2><p><strong>Животные</strong> — живые организмы, которые <strong>двигаются и питаются готовой пищей</strong>, могут реагировать на окружающую среду. Примеры: кошка, птица, рыба.</p><table style="width: 334px;"><colgroup><col style="width: 150px;"><col style="width: 184px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="150"><p>Тип</p></th><th colspan="1" rowspan="1" colwidth="184"><p>Примеры</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="150"><p>Позвоночные</p></td><td colspan="1" rowspan="1" colwidth="184"><p>Лев, слон, рыба</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="150"><p>Беспозвоночные</p></td><td colspan="1" rowspan="1" colwidth="184"><p>Медуза, паук, муравей</p></td></tr></tbody></table><h2>Вода в природе</h2><p><strong>Вода</strong> — прозрачная жидкость, необходимая для жизни всех живых организмов.</p><h3>Виды водоёмов</h3><table style="width: 474px;"><colgroup><col style="width: 132px;"><col style="width: 342px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="132"><p>Тип водоёма</p></th><th colspan="1" rowspan="1" colwidth="342"><p>Особенности</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Река</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Текущая вода, соединяет озёра и моря</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Озеро</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Стоячая вода, может быть пресной или солёной</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Море</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Солёная вода, часть океана</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="132"><p>Океан</p></td><td colspan="1" rowspan="1" colwidth="342"><p>Самый большой водоём, солёная вода</p></td></tr></tbody></table><img class="tiptap-resizable-image" src="http://localhost:5000/uploads/e9b46ecf-51d1-41cc-8ca5-3d416d4e0cdc.png" width="451" height="301"><h2>Воздух и атмосфера</h2><p><strong>Воздух</strong> — смесь газов, окружающая Землю, необходимая для дыхания всех живых существ.</p><p><strong>Атмосфера</strong> — газовая оболочка Земли, состоящая из слоёв воздуха.</p><h2>Погода и климат</h2><p><strong>Погода</strong> — состояние атмосферы в определённом месте и времени (дождь, снег, солнечно, ветер).</p><p><strong>Климат</strong> — среднее состояние погоды на большой территории за длительное время (годы, десятилетия).</p><h2>Почва</h2><p><strong>Почва</strong> — верхний слой земли, на котором растут растения.</p><h3>Состав почвы</h3><table style="width: 704px;"><colgroup><col style="width: 185px;"><col style="width: 519px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="185"><p>Компонент</p></th><th colspan="1" rowspan="1" colwidth="519"><p>Функция</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Минеральные вещества</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Дают растениям необходимые элементы (например, калий, фосфор, азот)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Вода</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Необходима для питания и жизни растений</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Воздух</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Корни дышат, получают кислород</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="185"><p>Органические вещества</p></td><td colspan="1" rowspan="1" colwidth="519"><p>Улучшают структуру почвы, делают её плодородной</p></td></tr></tbody></table><h2>Солнечные циклы Земли</h2><p><strong>День</strong> — период времени, когда Солнце видно над горизонтом.</p><p><strong>Ночь</strong> — период времени, когда Солнце находится ниже горизонта и свет не достигает</p><p><strong>Вращение Земли вокруг оси</strong> — движение, которое вызывает смену дня и ночи.</p><div data-block-type="note" data-type="info-block" class="info-block info-block-note"><ul><li><p>Земля вращается вокруг своей оси за <strong>24 часа</strong>.</p></li><li><p>Та часть Земли, которая повернута к Солнцу, испытывает <strong>день</strong>, противоположная часть — <strong>ночь</strong>.</p></li></ul></div><p><strong>Ось Земли</strong> — воображаемая линия, вокруг которой вращается Земля.</p><p><strong>Вращение Земли вокруг Солнца</strong> — движение, которое создаёт смену времён года.</p><div data-block-type="note" data-type="info-block" class="info-block info-block-note"><ul><li><p>Земля вращается вокруг Солнца за <strong>1 год</strong>.</p></li><li><p>Ось Земли наклонена примерно на <strong>23,5°</strong>, поэтому в разные времена года Солнце освещает разные части планеты по-разному.</p></li></ul></div><h2>Природные явления: дождь, снег, гроза, ветер</h2><p><strong>Природное явление</strong> — событие или процесс, происходящий в природе, без участия человека.</p><table style="width: 990px;"><colgroup><col style="width: 125px;"><col style="width: 383px;"><col style="width: 482px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="125"><p>Явление</p></th><th colspan="1" rowspan="1" colwidth="383"><p>Определение</p></th><th colspan="1" rowspan="1" colwidth="482"><p>Влияние на природу</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Дождь</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Осадки в виде капель воды</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Увлажняет почву, обеспечивает растения и животных водой</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Снег</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Осадки в виде кристаллов льда</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Увлажняет почву, формирует снежный покров, обеспечивает водой</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Гроза</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Атмосферное явление с дождём, громом и молниями</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Формирует дождевые осадки, может быть опасна для животных и людей</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="125"><p>Ветер</p></td><td colspan="1" rowspan="1" colwidth="383"><p>Движение воздуха</p></td><td colspan="1" rowspan="1" colwidth="482"><p>Переносит семена, пыльцу, влияет на погоду и климат</p></td></tr></tbody></table><h2>Охрана природы</h2><p><strong>Охрана природы</strong> — действия человека, направленные на сохранение природы и её ресурсов.</p><p><strong>Природные ресурсы</strong> — всё, что человек использует из природы: вода, растения, животные, воздух, почва.</p><p><strong>Экологическая ответственность</strong> — понимание влияния своих действий на природу и старание не причинять ей вред.</p><h3>Основные способы охраны природы</h3><table style="width: 684px;"><colgroup><col style="width: 164px;"><col style="width: 520px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="164"><p>Способ</p></th><th colspan="1" rowspan="1" colwidth="520"><p>Пример действий</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Сохранение воды</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не оставлять краны открытыми, использовать дождевую воду для полива</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Экономия энергии</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Выключать свет, использовать энергоэффективные лампы</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Защита растений</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не вырывать цветы, сажать новые растения</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Защита животных</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не обижать животных, помогать птицам зимой</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="164"><p>Чистота и порядок</p></td><td colspan="1" rowspan="1" colwidth="520"><p>Не бросать мусор, участвовать в субботниках</p></td></tr></tbody></table><p></p>', '#FDFF73', NULL, '2025-08-19 21:24:40', '2025-08-29 14:38:27', NULL, NULL, 0, 0),
	(6, 2, 'Github', 'Программирование', 'Полная информация о работе с Git. Так же про Github и инструменты, такие как GitLab.', '<p><strong>Git</strong> — это распределённая система управления версиями, которая позволяет отслеживать изменения в файлах и координировать совместную работу над проектами.<br><strong>GitHub</strong> — это веб-платформа и облачный сервис для хранения, управления и совместной работы с Git-репозиториями.</p><h2>Git Bash</h2><h3>Инициализация и настройка</h3><p><strong>git init</strong> - создаёт новый локальный репозиторий.</p><p><strong>git clone &lt;url&gt;</strong> -&nbsp; клонирует удалённый репозиторий на ваш компьютер.</p><p><strong>git config --global user.name "Username" </strong>- задаёт имя автора коммитов.</p><p><strong>git config --global user.email "example@domain,com"</strong> -&nbsp;задаёт почту автора.</p><h3>Работа с изменениями</h3><p><strong>git status </strong>- показывает текущее состояние репозитория (изменённые, добавленные, неотслеживаемые файлы).</p><p><strong>git add &lt;filename&gt; </strong>-&nbsp;добавляет файл в индекс для следующего коммита.</p><p><strong>git add .</strong> -&nbsp;добавляет все изменения в текущей папке.</p><p><strong>git commit -m "message" </strong>-&nbsp;создаёт коммит с описанием изменений.</p><h3>Работа с удалённым репозиторием</h3><p><strong>git push</strong> - отправка локальных изменений в удалённый репозиторий.</p><p><strong>git pull </strong>-&nbsp;получение и слияние изменений с удалённого репозитория.</p>', '#FDFF73', NULL, '2025-08-19 21:28:28', '2025-09-11 13:23:11', NULL, NULL, 0, 0),
	(7, 2, 'Lua', 'Программирование', 'Документация о скриптовом языке программирования Lua.', '<p><strong>Lua</strong> — скриптовый, встраиваемый, высокоуровневый язык программирования, созданный в Бразилии в 1993 году, применяемый для моддинга игр, автоматизации и расширения приложений.</p>', '#FDFF73', NULL, '2025-08-26 13:20:04', '2025-09-11 20:34:49', NULL, NULL, 0, 0),
	(8, 2, 'Изобразительное искусство', 'Визуальное искусство', 'Сведения о изобразительном искусстве.', '<p><strong>Изобразительное искусство</strong> — это способ выражения мыслей, эмоций и идей через визуальные образы: рисунок, живопись, скульптуру, графику, декоративно-прикладное искусство.</p><h2>Инструменты рисования</h2><h3>Краски</h3><p><strong>Акварель</strong> – прозрачная, легко смешивается, подходит для пейзажей и лёгких эффектов.<br><strong>Гуашь</strong> – плотная, более насыщенные цвета, хорошо подходит для детализированных работ.<br><strong>Акрил</strong> – быстро сохнет, яркие цвета, можно накладывать слоями.</p><h2>Теория цвета</h2><p><strong>Основные цвета</strong> — это цвета, которые нельзя получить смешиванием других цветов. Они являются базой для получения всех остальных цветов на цветовом круге.<br><strong>Красный</strong> — яркий, тёплый, эмоциональный.<br><strong>Синий</strong> — холодный, спокойный, глубокий.<br><strong>Жёлтый</strong> — солнечный, светлый, энергичный.</p><p><strong>Вторичные цвета</strong> — это цвета, которые получаются при <strong>смешивании двух основных цветов</strong> в равных или пропорциональных частях. Они являются промежуточными между основными и служат базой для создания всех остальных оттенков.</p><div data-block-type="note" data-type="info-block" class="info-block info-block-note"><p>Примеры смешения цветов:</p><ul><li><p>Оранжевый = красный + жёлтый</p></li><li><p>Зелёный = жёлтый + синий</p></li><li><p>Фиолетовый = синий + красный</p></li></ul></div><p><strong>Тёплые цвета</strong> — красный, оранжевый, жёлтый — активизируют, создают радость и энергию.<br><strong>Холодные цвета</strong> — синий, зелёный, фиолетовый — успокаивают, создают покой и пространство.</p><h2>Композиция</h2><p><strong>Композиция</strong> — это способ расположения всех элементов в работе так, чтобы взгляд зрителя двигался по рисунку логично и удобно.</p><h4><strong>Центр внимания</strong></h4><ul><li><p><strong>Главный объект</strong> — то, на что зритель смотрит первым.</p></li><li><p>Можно подчеркнуть размером, цветом или контрастом.</p></li><li><p>Пример: в натюрморте — яркое яблоко среди зелёных листьев.</p></li></ul><h4><strong>Баланс</strong></h4><ul><li><p>Визуальный баланс = равномерное распределение «тяжёлых» и «лёгких» элементов.</p></li><li><p>Существует <strong>симметричный</strong> (зеркальное отражение) и <strong>асимметричный</strong> (разные элементы, но одинаковое визуальное «вес»).</p></li></ul><h4><strong>Гармония</strong></h4><ul><li><p>Гармония = согласованность всех цветов, форм и линий.</p></li><li><p>Помогает работе быть «приятной для глаза», а не хаотичной.</p></li></ul><h2>Художественные приёмы</h2><p>Линии бывают <strong>прямые, волнистые, зигзагообразные, пунктирные</strong>.</p><p>Формы — <strong>круг, квадрат, треугольник, овал, прямоугольник</strong>.</p><h3>Фактура</h3><p><strong>Фактура</strong> — это ощущение поверхности предмета: гладкая, шероховатая, мягкая, твёрдая.</p><h3>Перспектива</h3><p><strong>Линейная перспектива</strong> — это способ построения перспективы с помощью прямых линий, сходящихся в одной или нескольких точках на горизонте.</p><table style="width: 1103px;"><colgroup><col style="width: 203px;"><col style="width: 291px;"><col style="width: 609px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="203"><p>Название</p></th><th colspan="1" rowspan="1" colwidth="291"><p>Описание</p></th><th colspan="1" rowspan="1" colwidth="609"><p>Зачем</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="203"><p>Одноточечная перспектива</p></td><td colspan="1" rowspan="1" colwidth="291"><p>Все линии сходятся в одной точке на горизонте</p></td><td colspan="1" rowspan="1" colwidth="609"><p>Используется для прямых дорог, коридоров, туннелей, когда объект направлен прямо к наблюдателю</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="203"><p>Двухточечная перспектива</p></td><td colspan="1" rowspan="1" colwidth="291"><p>Линии сходятся в двух точках на горизонте</p></td><td colspan="1" rowspan="1" colwidth="609"><p>Применяется для объектов, стоящих под углом к наблюдателю, например, углы зданий, коробки, мебель</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="203"><p>Трёхточечная перспектива</p></td><td colspan="1" rowspan="1" colwidth="291"><p>Добавляется третья точка для вертикальных линий</p></td><td colspan="1" rowspan="1" colwidth="609"><p>Для высоких зданий или объектов, которые нужно показать сверху или снизу, создаёт эффект высоты и глубины</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="203"><p>Горизонтальная линия</p></td><td colspan="1" rowspan="1" colwidth="291"><p>Линия, на уровне глаз наблюдателя</p></td><td colspan="1" rowspan="1" colwidth="609"><p>Определяет, где расположены точки схода и уровень, с которого мы смотрим на объекты</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="203"><p>Точка схода</p></td><td colspan="1" rowspan="1" colwidth="291"><p>Конкретная точка на горизонте, куда сходятся линии перспективы</p></td><td colspan="1" rowspan="1" colwidth="609"><p>Позволяет правильно уменьшать размеры объектов по мере удаления, создаёт иллюзию глубины</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="203"><p>Линии перспективы</p></td><td colspan="1" rowspan="1" colwidth="291"><p>Направляющие линии, идущие от объектов к точкам схода</p></td><td colspan="1" rowspan="1" colwidth="609"><p>Помогают рисовать объекты пропорционально и создавать ощущение трёхмерного пространства</p></td></tr></tbody></table><p></p><h2>Техники рисования</h2><p></p>', '#FDFF73', NULL, '2025-09-06 16:34:33', '2025-09-07 12:08:14', NULL, NULL, 0, 0),
	(9, 2, 'DST Modding', 'Программирование', 'Документация по моддингу игры Don\'t Starve Together.', '<p><strong>modinfo.lua</strong> - это метаописательный Lua-скрипт, обязательный для каждого мода в <em>Don’t Starve Together</em>.</p><h3>Полный список параметров:</h3><table style="width: 704px;"><colgroup><col style="width: 244px;"><col style="width: 108px;"><col style="width: 352px;"></colgroup><tbody><tr><th colspan="1" rowspan="1" colwidth="244"><p>Параметр</p></th><th colspan="1" rowspan="1" colwidth="108"><p>Значение / Тип</p></th><th colspan="1" rowspan="1" colwidth="352"><p>Описание</p></th></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>name</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>string</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Название мода, отображается в списке модов</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>description</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>string</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Описание мода, поясняющее его функционал</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>author</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>string</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Автор мода</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>version</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>string</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Версия мода (например, <code>"1.0.0"</code>)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>api_version</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>number</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Версия API DST (актуальная = <code>10</code>)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>dst_compatible</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Совместимость с DST</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>dont_starve_compatible</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Совместимость с оригинальной игрой <strong>Don’t Starve</strong></p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>reign_of_giants_compatible</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Совместимость с DLC <strong>Reign of Giants</strong></p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>shipwrecked_compatible</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Совместимость с DLC <strong>Shipwrecked</strong></p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>hamlet_compatible</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Совместимость с DLC <strong>Hamlet</strong></p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>client_only_mod</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Мод работает только на клиенте (не влияет на сервер)</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>all_clients_require_mod</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>true/false</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Если true, все игроки должны иметь этот мод для игры на сервере</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>icon_atlas</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>string (путь)</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Путь к <code>.xml</code> с описанием иконки мода</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>icon</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>string (путь)</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Путь к <code>.tex</code> файлу иконки</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>server_filter_tags</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>table</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Список тегов, по которым сервер можно найти в списке</p></td></tr><tr><td colspan="1" rowspan="1" colwidth="244"><p><code>configuration_options</code></p></td><td colspan="1" rowspan="1" colwidth="108"><p>table</p></td><td colspan="1" rowspan="1" colwidth="352"><p>Таблица с настройками мода (для меню конфигурации перед запуском мира)</p></td></tr></tbody></table><p><strong>AddClassPostConstruct(classname: string, postconstruct_fn: function(self))&nbsp;</strong>— это хук в системе моддинга Don’t Starve / Don’t Starve Together, который позволяет внедрить свою функцию в существующий класс после того, как он уже создан и проинициализирован.</p><ul><li><p><strong>classname</strong> -&nbsp;</p></li><li><p><strong>postconstruct_fn</strong> -&nbsp;функция, которая будет вызвана после создания экземпляра класса. Эта функция всегда получает экземпляр класса как self.</p></li></ul><p><strong>DoTaskInTime</strong> -&nbsp; это метод сущности inst, который <strong>создаёт отложенную задачу</strong>, выполняемую через заданное количество секунд.</p><p><strong>inst</strong> -&nbsp;это экземпляр сущности (entity).&nbsp;Любой объект в игре — будь то игрок, монстр, предмет, HUD, анимация — представлен как entity.</p><p><strong>components</strong> -&nbsp;набор динамических модулей, которые добавляют поведение, функциональность и возможности объекту во время игры.</p><p><strong>talker</strong> -&nbsp;это компонент, который позволяет объекту (обычно игроку) говорить текст в чат или отображать его над головой.</p><p><strong>Say("...") </strong>-&nbsp;это функция компонента talker, которая заставляет объект выводить текст в игре, чаще всего над головой.</p>', '#FDFF73', NULL, '2025-09-06 16:41:34', '2025-09-11 13:23:28', NULL, NULL, 0, 0),
	(10, 2, 'Linux', 'Операционные системы', 'Общмк сведения о работе с консолью в линукс, настройки системы, кастомизации.', '<p><strong>df</strong> - отображениет информацию о файловых системах, их размере, занятом и свободном дисковом пространстве.<br><em>-h</em> -&nbsp;human-readable, то есть «для людей читаемо». Она преобразует размер из килобайтов в KB, MB, GB, TB в зависимости от величины.</p><p><strong>Alacarte</strong> — это графический редактор меню для окружений рабочего стола <strong>GNOME</strong></p><p></p>', '#FDFF73', NULL, '2025-09-08 18:12:03', '2025-09-09 19:56:44', NULL, NULL, 0, 0),
	(11, 2, 'Музыка', 'Музыкальное искусство', 'Общая информация о музыке.', '<p><strong>Блюз</strong> - используются разные лады.</p>', '#FDFF73', NULL, '2025-09-08 18:26:44', '2025-09-08 18:26:44', NULL, NULL, 0, 0),
	(12, 2, 'Game Studies', 'Игры', 'Информация о играх как явлении и влиянии на культуру и прочее.', '<p><strong>Game Studies</strong> — академическая дисциплина, изучающая видеоигры и настольные игры как культурное, художественное, социальное и техническое явление.</p><p><strong>Лудология</strong> — это направление в Game Studies, которое изучает игры через их правила, механику и взаимодействие игрока с системой, а не через сюжеты и истории.</p><p><strong>Нарратология</strong> — направление в Game Studies, которое изучает сюжеты, истории, нарративы и способы их подачи в играх.</p><h2>История видеоигр</h2><p><strong>1950–1960 годы</strong> — это этап формирования и экспериментации в области интерактивных развлечений на компьютерах и аналоговых устройствах, когда игры существовали в основном как научные и образовательные проекты, а не коммерческие продукты. Этот период оказал фундаментальное влияние на последующую индустрию и культуру, заложив ключевые идеи: интерактивность, графическое отображение, моделирование физических и логических процессов, а также социальный и культурный потенциал игр.</p><p><strong>OXO</strong> — это <strong>однопользовательская компьютерная игра 1952 года</strong>, представляющая собой <strong>цифровую версию крестиков-ноликов</strong>, разработанную Александром Дугласом для компьютера <strong>EDSAC</strong> с целью изучения <strong>взаимодействия человека и машины</strong>.</p><p><strong>Sheep and Gates</strong> — это <strong>ранняя компьютерная игра 1952 года</strong>, разработанная Стэнли Гиллом для компьютера <strong>EDSAC</strong>, в которой игрок управляет овцой и выбирает, какие ворота открыть, чтобы пропустить её через забор, используя графическое отображение на экране.</p><p><strong>Tennis for Two</strong> — это <strong>аналоговая компьютерная игра 1958 года</strong>, разработанная физиком <strong>Уильямом Хигинботамом</strong> для Брукхейвенской национальной лаборатории, в которой два игрока управляют ракетками и отбивают мяч по двуммерной траектории, отображаемой на осциллоскопе.</p><h2>История настольных игр</h2><p><strong>Сенет</strong> — это древнеегипетская настольная игра, появившаяся примерно в <strong>3500–3100 гг. до н.э.</strong>. Она представляла собой <strong>доску с 30 клетками</strong>, по которой перемещались фишки в соответствии с бросками кости или жребием.</p><p><strong>Ludus Latrunculorum</strong> — это древнеримская настольная стратегическая игра, популярная примерно с I века до н.э. до III века н.э., своего рода прародитель современных тактических игр типа шахмат или шашек. Ходы по очереди — передвижение одной фишки на одну клетку в любом направлении. Захват фишки противника осуществляется, когда она оказывается поставленной между двумя фишками игрока по горизонтали или вертикали.</p><p></p>', '#FDFF73', NULL, '2025-09-10 18:19:57', '2025-09-10 20:07:19', NULL, NULL, 0, 0),
	(22, 2, 'павыпвапв', 'Разное', 'вапавп', '<p>павп</p>', '#FDFF73', NULL, '2025-09-11 20:41:36', '2025-09-11 20:41:36', 11, 12, 0, 0),
	(23, 2, 'Моды бля', 'Разное', 'лол', '', '#FDFF73', NULL, '2025-09-11 20:55:24', '2025-09-11 20:55:24', 11, 12, 0, 0);

-- Дамп структуры для таблица worldofmind.subcategories
CREATE TABLE IF NOT EXISTS `subcategories` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `category_id` int(10) unsigned NOT NULL,
  `name` varchar(100) NOT NULL,
  `color` varchar(20) DEFAULT '#2ecc71',
  `description` text DEFAULT NULL,
  `position_x` float DEFAULT 0,
  `position_y` float DEFAULT 0,
  `created_at` timestamp NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
